
public class BreakContinue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 for (int i=0;i<10 ; i++)
		 {
	 System.out.println("Value of I is " +i);
	 if(i==5)
	 {
		 System.out.println("Into break");
		 break;
	 }
	 else
	 {
		 System.out.println("Into continue");
		 continue;
	 }
		 }
	}

}
